import React, { Component } from 'react';
import styled from 'styled-components';

const SequenceLine = styled.div`
  height: 200px;
  display: grid;
  grid-template-columns: repeat(8, 1fr);
  grid-column-gap: 20px;
  grid-row-gap: 20px;
`;

const StyledCircle = styled.div`
  background-color: ${props => (props.onBeat) ? "red" : "grey"};
  box-shadow: rgba(0, 0, 0, 0.2) 0 -1px 7px 1px, inset #304701 0 -1px 9px, #89FF00 0 2px 12px;
  cursor: pointer;
  float: left;
  height: 50px;
  width: 50px;
  border-radius: 50%;
  align-self: center;
  justify-self: center;
  grid-column: ${props => props.number + 1};
  text-align: center;
`;

class BeatLine extends Component {
  render() {
    return (
      <SequenceLine>
        {this.props.sequence.map((value, idx) => {
          return(
            <StyledCircle key={idx} number={idx} onBeat={value}></StyledCircle>
          );
        })}
      </SequenceLine>
    );
  }
}

export default BeatLine;
