import React, { Component } from 'react';
import styled from 'styled-components';

const SequenceLine = styled.div`
  float: left;
`;

const StyledCircle = styled.div`
  background-color: ${props => (props.onBeat) ? "red" : "grey"};
  cursor: pointer;
  float: left;
  margin: 60px;
  height: 30px;
  width: 30px;
  border-radius: 50%;
  text-align: center;
`;

class BeatLine extends Component {
  render() {
    return (
      <SequenceLine>
        {this.props.sequence.map((value, idx) => {
          return(
            <StyledCircle key={idx} onBeat={value}></StyledCircle>
          );
        })}
      </SequenceLine>
    );
  }
}

export default BeatLine;
