import React, { Component } from 'react';

class Instrument extends Component {

  state = {
    sequence: new Array(8).fill(false)
  }

  updateSequence = (idx) => {
    let seq = this.state.sequence.forEach((val, jdx) => {
      if(idx === jdx) {
        return !this.state.sequence[idx];
      } else {
        return this.state.sequence[jdx];
      }
    })
    this.setState({
      sequence:  this.state.sequence.map((val, jdx) => {
        if(idx === jdx) {
          this.state.sequence[jdx] = !this.state.sequence[jdx];
        }
      })
    });

    console.log(this.state.sequence);

  }

  render() {
    let { sequence } = this.state;

    return(
      <div>
        {sequence.map((value, idx) => {
          return(
            <div key={idx} onClick={(idx) => this.updateSequence(idx)}> {idx} </div>
          );
        })}
      </div>
    );
  }
}

export default Instrument;
