import React, { Component } from 'react';
import styled from 'styled-components';

const SequenceLine = styled.div`
  float: left;
  background: black;
`

const StyledInstrument = styled.div`
  background-color: ${props => props.activated ? "red" : "lightgrey"};
  cursor: pointer;
  float: left;
  margin: 50px;
  height: 40px;
  line-height: 40px;
  width: 40px;
  border-radius: 5%;
  border: 10px solid pink;
  text-align: center;

`;

class Instrument extends Component {
  render() {
    return(
      <SequenceLine>
        {this.props.sequence.map((value, idx) => {
          return(
            <StyledInstrument key={idx} activated={this.props.sequence[idx]} onClick={() => this.props.onClick(idx)}></StyledInstrument>
          );
        })}
      </SequenceLine>
    );
  }
}

export default Instrument;
