import React, { Component } from 'react';
import { Howl, Howler } from 'howler';
import Instrument from './Instrument';

class Sequencer extends Component {

  state = {
    instruments: [
      new Array(8).fill(false),
      new Array(8).fill(false)
    ],
    sequenceNumber: 0,
    sequenceLength: 8,
    intervalPeriod: 500,
    intervalId: 1,
    sound: new Howl({
      src: ['/sounds/808-snare.wav']
    }),
    audioActivated: true
  }

  componentDidMount() {
    let intervalId = setInterval(this.timer, 500);
    this.setState({
      intervalId: intervalId
    })
  }

  timer = () => {
    let { sequenceNumber, sequenceLength, audioActivated } = this.state;

    if(audioActivated) {
      this.state.sound.play();
    }
  }

  updateSequence = (idx, jdx) => {
    let seq = this.state.instruments[idx].map((val, kdx) => {
      if(jdx === kdx) {
        return !this.state.instruments[idx][jdx];
      } else {
        return this.state.instruments[idx][kdx];
      }
    });

    let inst = this.state.instruments;
    inst[idx] = seq;

    this.setState({
      instruments:  inst
    });
  }


  render() {
    return (
      <div>
        {this.state.instruments.map((val, idx) => {
          return (
            <Instrument key={idx} sequence={this.state.instruments[idx]} onClick={(jdx) => this.updateSequence(idx, jdx)}/>
          )
        })}
      </div>
    );
  }
}

export default Sequencer;
