import React, { Component } from 'react';

class Instrument extends Component {

  state = {
    sequence: new Array(8).fill(false)
  }

  updateSequence = (idx) => {
    this.setState({
      sequence:  sequence.map((val, jdx) => {
        if(idx == jdx) {
          sequence[jdx] = !sequence[jdx];
        }
      })
    })

  }

  render() {
    let { sequence } = this.state;

    return(
      <div>
        {sequence.map((value, idx) => {
          return(
            <div key={idx} onClick={this.updateSequence(idx)}> {idx} </div>
          );
        })}
      </div>
    );
  }
}

export default Instrument;
