import React, { Component } from 'react';
import styled from 'styled-components';

const SequenceLine = styled.div`
  width: 1400px;
  height: 200px;
  display: grid;
  background: grey;
`

const StyledInstrument = styled.div`
  background-color: ${props => props.activated ? "#ff8181" : "#202020"};
  cursor: pointer;
  float: left;
  height: 40px;
  line-height: 40px;
  width: 40px;
  border-radius: 10%;
  border: 2px solid #181818;
  text-align: center;
  grid-column-start: ${props => props.key + 1};
  grid-column-end: ${props => props.key + 2};
  &:hover {
    box-shadow: 0px 0px 4px 4px rgba(255,255,255,1);
  }
`;

class Instrument extends Component {
  render() {
    return(
      <SequenceLine>
        {this.props.sequence.map((value, idx) => {
          return(
            <StyledInstrument key={idx} activated={this.props.sequence[idx]} onClick={() => this.props.onClick(idx)}></StyledInstrument>
          );
        })}
      </SequenceLine>
    );
  }
}

export default Instrument;
