  //
  //   let seq = this.state.sequence.map((val, jdx) => {
  //     if(idx === jdx) {
  //       return !this.state.sequence[idx];
  //     } else {
  //       return this.state.sequence[jdx];
  //     }
  //   });
  //
  //   this.setState({
  //     sequence:  seq
  //   });
  // }

  render() {
    let { sequence } = this.state;

    return(
      <div>
        {sequence.map((value, idx) => {
          return(
            <StyledInstrument className="sequence-button" key={idx} activated={this.props.sequence[idx]}> {idx} </StyledInstrument>
          );
        })}
      </div>
    );
  }
}

export default Instrument;
