import React, { Component } from 'react';
import './Instrument.css';
import styled from 'styled-components';

const StyledInstrument = styled.div`
  background-color: ${props => props.activated ? "red" : "pink"};
  cursor: pointer;
`;

class Instrument extends Component {
  render() {console.log(this.props.instrument);
    return(
      <div>
        {this.props.sequence.map((value, idx) => {
          return(
            <StyledInstrument className="sequence-button" key={idx} activated={this.props.sequence[idx]} onClick={() => this.props.onClick(idx)}> {idx + 1} </StyledInstrument>
          );
        })}
      </div>
    );
  }
}

export default Instrument;
